import React, { useState, useEffect } from "react";
import { Calendar, momentLocalizer } from "react-big-calendar";
import moment from "moment";
import "react-big-calendar/lib/css/react-big-calendar.css";
import "./Schedule.css";

const localizer = momentLocalizer(moment);

const Schedule = () => {
  const [events, setEvents] = useState([]);

  useEffect(() => {
    // 초기 이벤트 데이터를 설정합니다.
    setEvents([
      {
        title: "이벤트 1",
        start: new Date(2024, 5, 27, 16, 0), // 2024년 6월 27일 오후 4시
        end: new Date(2024, 5, 27, 18, 0), // 2024년 6월 27일 오후 6시
      },
      {
        title: "이벤트 2",
        start: new Date(2024, 5, 28, 8, 0), // 2024년 6월 28일 오전 8시
        end: new Date(2024, 5, 28, 9, 0), // 2024년 6월 28일 오전 9시
      },
    ]);
  }, []);

  const handleSelectSlot = ({ start, end }) => {
    const title = window.prompt("New Event name");
    if (title) {
      setEvents((prev) => [...prev, { start, end, title }]);
    }
  };

  return (
    <div className="schedule-container">
      <h2>Schedule</h2>
      <Calendar
        localizer={localizer}
        events={events}
        startAccessor="start"
        endAccessor="end"
        style={{ height: 600 }}
        selectable
        onSelectSlot={handleSelectSlot}
        views={["month"]}
        defaultView="month"
        popup
      />
    </div>
  );
};

export default Schedule;

