import React, { useState } from "react";
import Calendar from "react-calendar";
import "react-calendar/dist/Calendar.css";
import { format } from "date-fns";
import Modal from "react-modal";
import "./Schedule.css";
import { Calendar as BigCalendar, momentLocalizer } from "react-big-calendar";
import moment from "moment";
import "react-big-calendar/lib/css/react-big-calendar.css";

Modal.setAppElement("#root");

const localizer = momentLocalizer(moment);

const Schedule = ({ isLoggedIn }) => {
  const [events, setEvents] = useState([]);
  const [selectedDate, setSelectedDate] = useState(new Date());
  const [modalIsOpen, setModalIsOpen] = useState(false);
  const [newEvent, setNewEvent] = useState({
    title: "",
    start: new Date(),
    end: new Date(),
  });
  const [showLoginAlert, setShowLoginAlert] = useState(false);
  const [view, setView] = useState("month");

  const openModal = () => {
    if (isLoggedIn) {
      setModalIsOpen(true);
    } else {
      setShowLoginAlert(true);
      setTimeout(() => setShowLoginAlert(false), 2000); // 2초 후 메시지 숨기기
    }
  };

  const closeModal = () => {
    setModalIsOpen(false);
    setNewEvent({ title: "", start: new Date(), end: new Date() });
  };

  const handleDateChange = (date) => {
    setSelectedDate(date);
  };

  const handleEventChange = (e) => {
    const { name, value } = e.target;
    setNewEvent({ ...newEvent, [name]: value });
  };

  const handleAddEvent = () => {
    setEvents([...events, newEvent]);
    closeModal();
  };

  const handleViewChange = (view) => {
    setView(view);
  };

  return (
    <div className="schedule-container">
      {showLoginAlert && <div className="login-alert">로그인이 필요합니다</div>}
      <h1>Schedule</h1>
      <div className="view-buttons">
        <button onClick={() => handleViewChange("month")}>월간</button>
        <button onClick={() => handleViewChange("week")}>주간</button>
      </div>
      <button className="add-reservation-button" onClick={openModal}>
        Add Reservation
      </button>
      {view === "month" ? (
        <Calendar onClickDay={handleDateChange} value={selectedDate} />
      ) : (
        <BigCalendar
          localizer={localizer}
          events={events}
          startAccessor="start"
          endAccessor="end"
          style={{ height: 500, margin: "50px" }}
          defaultView={view}
        />
      )}
      <Modal
        isOpen={modalIsOpen}
        onRequestClose={closeModal}
        className="modal"
        overlayClassName="overlay"
      >
        <h2>Add New Event</h2>
        <form>
          <input
            type="text"
            name="title"
            placeholder="Event Title"
            value={newEvent.title}
            onChange={handleEventChange}
          />
          <input
            type="datetime-local"
            name="start"
            value={format(new Date(newEvent.start), "yyyy-MM-dd'T'HH:mm")}
            onChange={handleEventChange}
          />
          <input
            type="datetime-local"
            name="end"
            value={format(new Date(newEvent.end), "yyyy-MM-dd'T'HH:mm")}
            onChange={handleEventChange}
          />
          <button type="button" onClick={handleAddEvent}>
            Add Event
          </button>
          <button type="button" onClick={closeModal}>
            Cancel
          </button>
        </form>
      </Modal>
      <div className="events">
        {events.map((event, index) => (
          <div key={index} className="event">
            <h3>{event.title}</h3>
            <p>
              {format(new Date(event.start), "yyyy-MM-dd HH:mm")} -{" "}
              {format(new Date(event.end), "yyyy-MM-dd HH:mm")}
            </p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Schedule;
