import React, { useState } from "react";
import Calendar from "react-calendar";
import "react-calendar/dist/Calendar.css";
import { Calendar as BigCalendar, momentLocalizer } from "react-big-calendar";
import moment from "moment";
import "react-big-calendar/lib/css/react-big-calendar.css";
import "./Calendar.css";

const localizer = momentLocalizer(moment);

const ScheduleCalendar = () => {
  const [date, setDate] = useState(new Date());
  const [events, setEvents] = useState([]);
  const [showModal, setShowModal] = useState(false);
  const [eventText, setEventText] = useState("");
  const [eventTime, setEventTime] = useState("");

  const onChange = (date) => {
    setDate(date);
  };

  const onDoubleClickDay = (date) => {
    setDate(date);
    setShowModal(true);
  };

  const handleAddEvent = () => {
    const newEvent = {
      title: eventText,
      start: new Date(`${date.toDateString()} ${eventTime}`),
      end: new Date(`${date.toDateString()} ${eventTime}`),
    };
    setEvents([...events, newEvent]);
    setShowModal(false);
    setEventText("");
    setEventTime("");
  };

  const handleTodayClick = () => {
    const today = new Date();
    setDate(today);
  };

  const tileClassName = ({ date }) => {
    const day = date.getDay();
    if (day === 6) {
      return "saturday";
    }
    return null;
  };

  return (
    <div className="calendar-container">
      <button className="today-button" onClick={handleTodayClick}>
        Today
      </button>
      <Calendar
        onChange={onChange}
        value={date}
        onClickDay={onChange}
        onDoubleClickDay={onDoubleClickDay}
        tileClassName={tileClassName}
      />
      <div style={{ height: 500, width: "100%", marginTop: 20 }}>
        <BigCalendar
          localizer={localizer}
          events={events}
          startAccessor="start"
          endAccessor="end"
          defaultView="week"
          views={["month", "week"]}
          date={date}
          onNavigate={(newDate) => setDate(newDate)}
        />
      </div>

      {showModal && (
        <div className="modal">
          <div className="modal-content">
            <h4>Add Event</h4>
            <input
              type="text"
              value={eventText}
              onChange={(e) => setEventText(e.target.value)}
              placeholder="Event description"
            />
            <input
              type="time"
              value={eventTime}
              onChange={(e) => setEventTime(e.target.value)}
              placeholder="Event time"
            />
            <button onClick={handleAddEvent}>Add</button>
            <button onClick={() => setShowModal(false)}>Cancel</button>
          </div>
        </div>
      )}
    </div>
  );
};

export default ScheduleCalendar;
