import React, { useState } from "react";
import Calendar from "react-calendar";
import "react-calendar/dist/Calendar.css";
import "./Schedule.css";
import {
  Calendar as WeeklyCalendar,
  momentLocalizer,
} from "react-big-calendar";
import moment from "moment";
import "react-big-calendar/lib/css/react-big-calendar.css";

const localizer = momentLocalizer(moment);

const Schedule = () => {
  const [date, setDate] = useState(new Date());
  const [events, setEvents] = useState([]);
  const [selectedDate, setSelectedDate] = useState(new Date());
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [newEvent, setNewEvent] = useState({
    title: "",
    start: new Date(),
    end: new Date(),
  });

  const handleDateClick = (date) => {
    setSelectedDate(date);
  };

  const handleEventClick = (event) => {
    const { title, start, end } = event;
    const newTitle = prompt("Edit event title", title);
    if (newTitle) {
      setEvents(
        events.map((evt) => (evt === event ? { ...evt, title: newTitle } : evt))
      );
    }
  };

  const handleAddEvent = (slotInfo) => {
    const title = prompt("New Event name");
    if (title) {
      setEvents([
        ...events,
        { title, start: slotInfo.start, end: slotInfo.end },
      ]);
    }
  };

  const handleDoubleClickEvent = (event) => {
    if (window.confirm(`Do you want to delete the event '${event.title}'?`)) {
      setEvents(events.filter((evt) => evt !== event));
    }
  };

  const openModal = () => {
    setIsModalOpen(true);
  };

  const closeModal = () => {
    setIsModalOpen(false);
  };

  const handleEventChange = (field, value) => {
    setNewEvent({ ...newEvent, [field]: value });
  };

  const handleSaveEvent = () => {
    setEvents([
      ...events,
      {
        ...newEvent,
        start: new Date(newEvent.start),
        end: new Date(newEvent.end),
      },
    ]);
    closeModal();
  };

  return (
    <div className="schedule-container">
      <h2>Schedule</h2>
      <button onClick={openModal}>Add Reservation</button>
      <div className="calendar-container">
        <Calendar
          className="monthly-calendar"
          onChange={setDate}
          value={date}
          onClickDay={handleDateClick}
        />
        <WeeklyCalendar
          className="weekly-calendar"
          localizer={localizer}
          events={events}
          startAccessor="start"
          endAccessor="end"
          defaultView="week"
          views={["week"]}
          onSelectSlot={handleAddEvent}
          selectable
          onDoubleClickEvent={handleDoubleClickEvent}
          style={{ height: 500, width: "100%" }}
        />
      </div>

      {isModalOpen && (
        <div className="modal">
          <div className="modal-content">
            <h3>Add Event</h3>
            <input
              type="text"
              placeholder="Event Title"
              value={newEvent.title}
              onChange={(e) => handleEventChange("title", e.target.value)}
            />
            <input
              type="datetime-local"
              value={moment(newEvent.start).format("YYYY-MM-DDTHH:mm")}
              onChange={(e) => handleEventChange("start", e.target.value)}
            />
            <input
              type="datetime-local"
              value={moment(newEvent.end).format("YYYY-MM-DDTHH:mm")}
              onChange={(e) => handleEventChange("end", e.target.value)}
            />
            <button onClick={handleSaveEvent}>Save</button>
            <button onClick={closeModal}>Cancel</button>
          </div>
        </div>
      )}
    </div>
  );
};

export default Schedule;
